import React, { useState } from 'react';
import './FAQ.css';

const FAQ = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const toggleFAQ = index => {
    if (activeIndex === index) {
      setActiveIndex(null);
    } else {
      setActiveIndex(index);
    }
  };

  return (
    <section className="faq-section">
      <h2>Frequently Asked Questions for PC General Contracting Corp</h2>
      <h3>General Contractor Services in NYC and Surrounding Areas</h3>
      <div className="faq-container">
        {faqData.map((faq, index) => (
          <div key={index} className="faq-item">
            <div
              className={`faq-question ${activeIndex === index ? 'active' : ''}`}
              onClick={() => toggleFAQ(index)}
            >
              {faq.question}
              <span className="faq-icon"></span>
            </div>
            <div className={`faq-answer ${activeIndex === index ? 'active' : ''}`}>
              <div className="faq-answer-content">{faq.answer}</div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

const faqData = [
  {
    question: 'What services does Brick Tech Contracting Corp provide?',
    answer: 'Brick Tech Contracting Corp provides a range of services including masonry, roofing, waterproofing, power washing, and more.'
  },
  {
    question: 'How can I contact Brick Tech Contracting Corp?',
    answer: 'You can contact us via phone at (718)-200-0699 or email us at bricktechnyc@gmail.com.'
  },
  {
    question: 'What areas does Brick Tech Contracting Corp serve?',
    answer: 'We serve the entire NYC area including Brooklyn, Queens, and Manhattan.'
  },
  {
    question: 'How do I choose the right general contractor for my construction project in NYC?',
    answer: 'To choose the right general contractor in NYC, consider their experience, credentials, client reviews, and the range of services they offer. Schedule consultations to discuss your project and assess their communication and project management skills.'
  },
  {
    question: 'Where does Brick Tech Contracting Corp operate?',
    answer: 'We operate throughout the NYC area, providing top-quality general contractor services in Brooklyn, Queens, and Manhattan.'
  },
  {
    question: 'Is Brick Tech Contracting Corp a licensed contractor?',
    answer: 'Yes, Brick Tech Contracting Corp is a fully licensed and insured construction company in NYC.'
  },
  {
    question: 'Is Brick Tech Contracting Corp insured?',
    answer: 'Yes, we are fully insured to protect your investment and provide peace of mind.'
  },
  {
    question: 'Can Brick Tech Contracting Corp handle both commercial and residential projects?',
    answer: 'Yes, we have experience in handling both commercial and residential projects of all sizes and complexities.'
  }
];

export default FAQ;
