import React, { useState, useEffect } from 'react';
import './Navbar.css';
import logo from './Pictures/Logo.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPhone, faMobileAlt } from '@fortawesome/free-solid-svg-icons';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [subDropdownOpen, setSubDropdownOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 60);
    };

    const handleResize = () => {
      if (window.innerWidth > 768) {
        setIsOpen(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const toggleSubDropdown = () => {
    setSubDropdownOpen(!subDropdownOpen);
  };

  return (
    <header className="navbar">
      <div className="navbar__top">
        <div className="navbar__contact">
          <span>
            <FontAwesomeIcon icon={faPhone} /> Office: (718)-200-0699
          </span>
          <span>
            <FontAwesomeIcon icon={faMobileAlt} /> Cell: (718)-300-7014
          </span>
          <span>30+ Years Experience | Fully Licensed | Fully Insured | MBE Certified Contractor</span>
        </div>
        <button className="navbar__estimate">Free Estimate</button>
      </div>
      <div className={`navbar__main ${scrolled ? 'scrolled' : ''}`}>
        <div className="navbar__brand">
          <img src={logo} alt="Company Logo" className="navbar__logo navbar__logo--desktop" />
        </div>
        <nav className={`navbar__menu ${isOpen ? 'navbar__menu--open' : ''}`}>
          <a href="#home" className="navbar__link">Home</a>
          <a href="#about" className="navbar__link">About Us</a>
          <div className="navbar__link navbar__dropdown" onClick={toggleDropdown}>
            Services
            <div className={`navbar__dropdown-menu ${dropdownOpen ? 'open' : ''}`}>
              <div className="navbar__dropdown-item" onClick={toggleSubDropdown}>
                Masonry Services
                <div className={`navbar__sub-dropdown-menu ${subDropdownOpen ? 'open' : ''}`}>
                  <a href="#brick-pointing" className="navbar__sub-dropdown-link">Brick Pointing</a>
                  <a href="#brick-repair" className="navbar__sub-dropdown-link">Brick Repair & Replacement</a>
                  <a href="#brick-work" className="navbar__sub-dropdown-link">Brick Work</a>
                  <a href="#chimney" className="navbar__sub-dropdown-link">Chimney</a>
                  <a href="#driveway-repair" className="navbar__sub-dropdown-link">Driveway Repair</a>
                  <a href="#pavers" className="navbar__sub-dropdown-link">Pavers</a>
                  <a href="#retaining-walls" className="navbar__sub-dropdown-link">Retaining Walls</a>
                  <a href="#sidewalk-repair" className="navbar__sub-dropdown-link">Sidewalk Repair</a>
                  <a href="#sidewalk-violation" className="navbar__sub-dropdown-link">Sidewalk Violation Removal</a>
                  <a href="#stoops-porches" className="navbar__sub-dropdown-link">Stoops & Porches</a>
                  <a href="#stucco" className="navbar__sub-dropdown-link">Stucco</a>
                  <a href="#thorocoat-thoroseal" className="navbar__sub-dropdown-link">Thorocoat & Thoroseal</a>
                  <a href="#veneers-pillars" className="navbar__sub-dropdown-link">Veneers Pillars</a>
                </div>
              </div>
              <a href="#roofing" className="navbar__dropdown-link">Roofing</a>
              <a href="#siding" className="navbar__dropdown-link">Siding</a>
              <a href="#gutter" className="navbar__dropdown-link">Gutter</a>
              <a href="#waterproofing" className="navbar__dropdown-link">Waterproofing Services</a>
              <a href="#variety-services" className="navbar__dropdown-link">Variety of Services</a>
              <a href="#local-law" className="navbar__dropdown-link">Local LAW 11 & 10</a>
            </div>
          </div>
          <a href="#portfolio" className="navbar__link">Our Portfolio</a>
          <a href="#blog" className="navbar__link">Blog</a>
          <a href="#contact" className="navbar__link">Contact Us</a>
        </nav>
        <button className={`navbar__toggle ${isOpen ? 'navbar__toggle--open' : ''}`} onClick={toggleMenu}>
          <div className={`navbar__toggle-icon ${isOpen ? 'navbar__toggle-icon--open' : ''}`}></div>
        </button>
      </div>
      {isOpen && (
        <div className="navbar__mobile-menu">
          <button className="navbar__close" onClick={toggleMenu}>×</button>
          <a href="#home" className="navbar__mobile-link" onClick={toggleMenu}>Home</a>
          <a href="#about" className="navbar__mobile-link" onClick={toggleMenu}>About Us</a>
          <a href="#services" className="navbar__mobile-link" onClick={toggleMenu}>Services</a>
          <a href="#portfolio" className="navbar__mobile-link" onClick={toggleMenu}>Our Portfolio</a>
          <a href="#blog" className="navbar__mobile-link" onClick={toggleMenu}>Blog</a>
          <a href="#contact" className="navbar__mobile-link" onClick={toggleMenu}>Contact Us</a>
          <div className="navbar__contact-mobile">
            <span>
              <FontAwesomeIcon icon={faPhone} /> Office: (718)-200-0699
            </span>
            <span>
              <FontAwesomeIcon icon={faMobileAlt} /> Cell: (718)-300-7014
            </span>
            <span>30+ Years Experience | Fully Licensed | Fully Insured | MBE Certified Contractor</span>
          </div>
          <button className="navbar__estimate-mobile">Free Estimate</button>
        </div>
      )}
    </header>
  );
};

export default Navbar;
